import './articlesingle.css'
import { Link } from 'react-router-dom'
import React, {useState, useEffect} from 'react'
// import axios from 'axios'
import { useParams } from 'react-router'



const Articlesingle = ( ) =>{
    const params = useParams();
    const [data, setData ] = useState();

    useEffect(() => {
        fetch(`http://127.0.0.1:8000/api/article/${params.id}`, {})
        .then((res) => res.json())
        .then((response) => {
            setData(response);
            console.log(`http://127.0.0.1:8000/api/article/${params.id}`);
        })
        .catch((error) => console.log(error))
    }, [params.id]);
    return(
        <div> 
        <div className="article-heading my-20 mx-0 flex text-left">
            <div class="heading-text">
                <h4 class="link"><Link to="/articles"><i class="bi bi-arrow-left"></i>BACK</Link></h4>
                <h1 className="text-6xl my-20">Decide when to stop designing before you begin</h1>
                <h4 className="text-2xl">14 January, 2021 • 3 min read</h4>
            </div>
            <div class="heading-image">
                <img src="/articlepic.webp" alt="" className="rounded-2xl"/>
            </div>
        </div>

        <div className="article-content my-0 mx-auto ">
                return(
                    <div>
                        {/* <p>{ data.title }</p> */}
                        <p>{ data.content }</p>
                    </div>          
                )           
        </div>
    </div>
    )
}

export default Articlesingle;

