import './articlepage.css'
import { Link } from 'react-router-dom'
import axios from 'axios';
import React from 'react'

class Articlepage extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            articles: [],
        }
        this.fetchTask = this.fetchTask.bind(this)
    }
    componentWillMount(){
        this.fetchTask()
    }

    fetchTask(){
        console.log('Fetching.....')

        axios.get('http://127.0.0.1:8000/api/')
        .then(response => 
            this.setState({
                articles : response.data
            })
        )
        .catch((err) => {
            console.log(err)
        })
    }

    render(){
        var articles = this.state.articles
        return (
            <div id="articles-content" className='my-60 mx-auto text-left'>
                <h1 className="text-6xl mb-16 relative">Articles</h1>
                { articles.map((article) => {
                    return(
                        <div className="articlepage-card text-xl p-16 max-w-4xl md:max-w-max cursor-pointer my-20">
                        <h3 className="mb-7 text-5xl font-semibold">{ article.title} </h3>
                        <p className="mb-7 text-4xl">{ article.content }</p>
                        <Link to={`/article/${article.id}`} className="font-bold hover:no-underline text-4xl">Read more <i class="bi bi-arrow-right"></i></Link>
                    </div>
                            ) 
                })}            
             </div>
        )
}
}
 
export default Articlepage;
