import './article.css'
import React from 'react'


class Articles extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            articles : [], 
        }
        this.fetchTask = this.fetchTask.bind(this)
    }

    componentWillMount(){
        this.fetchTask()
    }


    fetchTask(){
        console.log('Fetching.....')

        fetch('http://127.0.0.1:8000/api/')
        .then(response => response.json())
        .then(data => 
            this.setState({
                 articles: data
             })
            )
    }
    render(){
        var articles = this.state.articles
        return(
        <div id="articles">
            <h2 className="text-6xl font-semibold text-left pl-20">Articles</h2>
            <div className="article-cards">
                { articles.map(function(article, index){
                    return(
                        <div className="article-card one" key={index}>
                            <h3>{article.title}</h3>
                            <h3>3 mins</h3>
                        </div>
                    )
                }) }
                
                 {/* 
                 
                <div className="article-card two">
                    <h3>Decide when to stop designing before you begin</h3>
                    <h3>3 mins</h3>
                </div>
                <div className="article-card three">
                    <h3>Decide when to stop designing before you begin</h3>
                    <h3>3 mins</h3>
                </div>
                <div className="article-card four">
                    <h3>Decide when to stop designing before you begin</h3>
                    <h3>3 mins</h3>
                </div>
                 
                 */}

            </div>
        </div>
        )
    }


}

 
export default Articles;
