import './articlesingle.css'
import { Link } from 'react-router-dom'
import React, {useState, useEffect} from 'react'
import axios from 'axios'



const Articlesingle = ({ match }) =>{
    const [article, setArticle ] = useState([]);

    useEffect(()=>{
        fetchArticle();
    })

    const fetchArticle = () => {
        axios.get(`http://127.0.0.1:8000/api/article/${match.params.id}`)
        .then((res) => {
            setArticle(res.article);
            console.log("got it");
        })
        .catch((err) => console.log(err));
    }

    return(
        <div> 
        <div className="article-heading my-20 mx-0 flex text-left">
            <div class="heading-text">
                <h4 class="link"><Link to="/articles"><i class="bi bi-arrow-left"></i>BACK</Link></h4>
                <h1 className="text-6xl my-20">Decide when to stop designing before you begin</h1>
                <h4 className="text-2xl">14 January, 2021 • 3 min read</h4>
            </div>
            <div class="heading-image">
                <img src="/articlepic.webp" alt="" className="rounded-2xl"/>
            </div>
        </div>

        <div className="article-content my-0 mx-auto ">
            {articles.map(function(article, index){
                return(
                    <div key={index}>
                        <p>{ article.title }</p>
                        <p>{ article.content }</p>
                    </div>          
                ) 
            })}
           
        </div>
    </div>
    )
}

export default Articlesingle;

