import './articlesingle.css'
import { Link } from 'react-router-dom'
import React from 'react'

/*
const Articlesingle = () => {
    return ( 
        <div> 
            <div className="article-heading my-20 mx-0 flex text-left">
                <div class="heading-text">
                    <h4 class="link"><Link to="/articles"><i class="bi bi-arrow-left"></i>BACK</Link></h4>
                    <h1 className="text-6xl my-20">Decide when to stop designing before you begin</h1>
                    <h4 className="text-2xl">14 January, 2021 • 3 min read</h4>
                </div>
                <div class="heading-image">
                    <img src="/articlepic.webp" alt="" className="rounded-2xl"/>
                </div>
            </div>

            <div className="article-content my-0 mx-auto ">
                <p>
                    
                </p>
            </div>
        </div>
       
     );
}
 
export default Articlesingle;

*/



class Articlesingle extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            article:{},
            articleId: props.match.params.id
        }
        this.fetchTasks = this.fetchTasks.bind(this)
    };

    componentWillMount(){
        this.fetchTasks()
    }
    fetchTasks(){
        console.log("Fetching.....")

        fetch(`http://127.0.0.1:8000/api/article/`)
         .then(response => response.json())
         .then(data => 
            this.setState({
                articles: data
            }))
    }
    render(){
        var articles = this.state.articles
        return(
            <div> 
            <div className="article-heading my-20 mx-0 flex text-left">
                <div class="heading-text">
                    <h4 class="link"><Link to="/articles"><i class="bi bi-arrow-left"></i>BACK</Link></h4>
                    <h1 className="text-6xl my-20">Decide when to stop designing before you begin</h1>
                    <h4 className="text-2xl">14 January, 2021 • 3 min read</h4>
                </div>
                <div class="heading-image">
                    <img src="/articlepic.webp" alt="" className="rounded-2xl"/>
                </div>
            </div>

            <div className="article-content my-0 mx-auto ">
                {articles.map(function(article, index){
                    return(
                        <div key={index}>
                            <p>{ article.title }</p>
                            <p>{ article.content }</p>
                        </div>          
                    ) 
                })}
               
            </div>
        </div>
        )
    }
}

export default Articlesingle;
